const express = require('express');
const cors = require('cors');

const app = express();

// Middleware para permitir todas as origens temporariamente (para fins de desenvolvimento)
app.use(cors());

// Rota para receber dados de aluno
app.post('/aluno', (req, res) => {
    // Aqui você pode processar os dados enviados pelo formulário
    const { matricula, nome, cpf, endereco } = req.body;
    
    // Exemplo de resposta
    res.json({ message: 'Cadastro realizado com sucesso', aluno: { matricula, nome, cpf, endereco } });
});

// Inicialização do servidor na porta 8080
const PORT = 8080;
app.listen(PORT, () => {
    console.log(`Servidor rodando na porta ${PORT}`);
});
